-- A dual-number and list-based version of a function that goes
-- from `R^3` to `R`.
fooD :: forall r n d. (ADModeAndNum d r, KnownNat n)
     => [ADVal d (OR.Array n r)] -> ADVal d (OR.Array n r)
fooD [x, y, z] =
  let w = x * sin y
  in atan2 z w + z * w
fooD _ = error "wrong number of arguments"

