fooBuild3 :: forall r n.
             ( ADReady r, KnownNat n, RealFloat (TensorOf n r) )
          => TensorOf (1 + n) r -> TensorOf (1 + n) r
fooBuild3 v =
  tbuild1 22 $ \ix ->
    bar ( tkonst0N (tailShape $ tshape v) 1
        , tindex v [min 1 (ix + 1)] )  -- index not out of bounds

testFooBuild3 :: Assertion
