nestedBuildMap r =
  let w = tkonst0N [4]
      v' = tkonst0N [177] r :: TensorOf 1 r
      nestedMap x = tmap0N (x /) (w (tscalar x))
      variableLengthBuild iy = tbuild1 7 (\ix ->
        tindex v' [ix + iy])
      doublyBuild =
        tbuild1 3 (tkonst0N (takeShape @n @(6 - n)
                             $ 2 :$ 4 :$ 2 :$ 1 :$ 3 :$ 2 :$ ZS)
                   . tminimum0 . variableLengthBuild)
  in tmap0N (\x -> x
                   * tunScalar (tsum0
                       (tbuild1 3 (\ix -> bar ( tscalar x
                                              , tindex v' [ix]) )
                        + fooBuild1 (nestedMap x)
                        / fooMap1 [3] (tscalar x)))
            ) doublyBuild

testNestedBuildMap1 :: Assertion
testNestedBuildMap1 =
  assertEqualUpToEpsilon' 1e-8
    22.673212907588812
    (rev' @(OR.Array 1 Double) nestedBuildMap 0.6)

testNestedBuildMap7 :: Assertion
testNestedBuildMap7 =
  assertEqualUpToEpsilon' 1e-8
    2176.628439128524
    (rev' @(OR.Array 7 Double) nestedBuildMap 0.6)

